<?php    use yii\helpers\Html;    use yii\bootstrap\ActiveForm;    //use budyaga_cust\users\components\AuthChoice;    use yii\helpers\Url;    $post = Yii::$app->request->post();    $newpassword = 'swal({             title: "'.Yii::t('yii', 'Congratulations').'",         text: "'.Yii::t('users', 'NEW_PASSWORD_WAS_SAVED').'",         type: "success"    });';    $act1 = 'swal({             title: "'.Yii::t('yii', 'Congratulations').'",         text: "To your e-mail '.Yii::$app->session->getFlash('email').', a username and password has been sent, open this letter and follow the instructions.",         type: "success"    });';    $act = 'swal({             title: "Успешно",         text: "'.Yii::t('users', 'CHECK_YOUR_EMAIL_FOR_FURTHER_INSTRUCTION_CANGE_PASS').'",         type: "success"    });';    $check_your_email = 'swal({             title: "Успешно",         text: "'.Yii::t('users', 'CHECK_YOUR_EMAIL_FOR_FURTHER_INSTRUCTION_ACTIVATION', ['email' => $post['SignupForm']['email']]).'",         type: "success"    });';    $erract = 'swal({             title: "'.Yii::t('yii', 'Error').'",         text: "'.Yii::t('users', 'CAN_NOT_SEND_EMAIL_FOR_CONFIRMATION').'",         type: "error"    });';    $can_not_send = 'swal({             title: "'.Yii::t('yii', 'Error').'",         text: "'.Yii::t('users', 'CAN_NOT_SEND_EMAIL_FOR_CONFIRMATION').'",         type: "error"    });';    $can_not_create = 'swal({             title: "'.Yii::t('yii', 'Error').'",         text: "'.Yii::t('users', 'CAN_NOT_CREATE_NEW_USER').'",         type: "error"    });';    (Yii::$app->session->hasFlash('NEWPASSWORD')) ? $this->registerJs($newpassword) : '';    (Yii::$app->session->hasFlash('OkResult')) ? $this->registerJs($act1) : '';    (Yii::$app->session->hasFlash('OKREZET')) ? $this->registerJs($act) : '';    (Yii::$app->session->hasFlash('ErrorREZET')) ? $this->registerJs($erract) : '';    (Yii::$app->session->hasFlash('CHECK_YOUR_EMAIL')) ? $this->registerJs($check_your_email) : '';    (Yii::$app->session->hasFlash('CAN_NOT_SEND')) ? $this->registerJs($can_not_send) : '';    (Yii::$app->session->hasFlash('CAN_NOT_CREATE')) ? $this->registerJs($can_not_create) : '';        $this->title = 'CMS Defina';    $this->params['breadcrumbs'][] = $this->title;    $x = 'Мы не храним ваши данные и пароли, это делает ваше устройство. Если вам удобнее вводить свой e-mail самостоятельно, уберите галочку "Запомнить меня"';    $y = 'We do not store your data and passwords, it makes your device. If it is more convenient for you to enter your e-mail address yourself, uncheck "Remember me"';    $fieldOptions1 = [        'options' => ['class' => 'form-group has-feedback'],        'inputTemplate' => "{input}<span class='fa fa-fw fa-envelope form-control-feedback'></span>"    ];    $fieldOptions2 = [        'options' => ['class' => 'form-group has-feedback'],        'inputTemplate' => "{input}<span class='fa fa-fw fa-lock form-control-feedback'></span>"    ];    $rememberMe = [        'options' => ['id' => 'customcheck'],        'inputTemplate' => '{input}<span class="checkmark"></span>'    ];        $this->registerCss('        #loginblockinfo a {            color:#fff !important;        }    ');?><div class="login-box">    <div class="login-logo text-center">    <?=$this->render('_logo');?>    <div class="register-box-body"><!--        <div id="loginblockinfo"></div>-->        <?php $form = ActiveForm::begin(['id' => 'login-form']); ?>            <div id="csrf"></div>            <?= $form->field($model, 'email', $fieldOptions1)->textInput(['type' => 'email', 'placeholder' => Yii::t('users', 'YOURLOGIN')])->label(false);?>            <?= $form->field($model, 'password', $fieldOptions2)->passwordInput(['placeholder' => Yii::t('users', 'YOURPASS')])->label(false);?>                <div class="row">                    <div class="col-xs-8 icheck">                        <?= $form->field($model, 'rememberMe', $rememberMe)->checkbox() ?>                    </div>                    <div class="col-xs-4">                        <?= Html::submitButton(Yii::t('yii', 'Sign In'), ['class' => 'btn btn-danger btn-block btn-flat', 'name' => 'login-button']) ?>                    </div>                </div>                <hr/>                <div style="font-size: 13px;">                    <?= Yii::t('users', 'YOU_CAN_RESET_PASSWORD', ['url' => Url::toRoute('/user/user/request-password-reset')])?><br>                    <?= Yii::t('users', 'YOU_CAN_REGISTR', ['url' => '/signup']) // Url::toRoute('/user/user/signup')?>                    </div>        <?php ActiveForm::end(); ?>        <div id="loginblock" style="display:none;"><?=Yii::t('users', 'LOGINBLOCK');?></div>        <div id="loginblockurl" style="display:none;"><?=Html::a(Yii::t('users', 'MORE'), '/doc/oferta');?></div>    </div>        <div class="brblock"></div>    <?=Html::tag('p',(Yii::$app->language == 'ru') ? $x : $y, ['class' => 'text-muted', 'style' => 'font-size:12px;']);?>    </div></div>