<?php    use yii\helpers\Html;    use yii\bootstrap\ActiveForm;    $this->title = Yii::t('users', 'REQUEST_PASSWORD_RESET');    $this->params['breadcrumbs'][] = $this->title;    $title = (Yii::$app->language == 'ru') ? 'Здесь нужна ваша электронная почта, указанная при регистрации' : 'Here you need your email specified during registration';    $fieldOptions2 = [        'template' => '{label}<div class="input-group">{input}            <span class="input-group-btn">'.Html::submitButton(Yii::t('yii', 'Send', ['icon' => '<i class="fa fa-paper-plane"></i>']), ['class' => 'btn btn-danger']).'</span>            </div>{hint}{error}'    ];?><div class="login-box">    <?=$this->render('_logo');?>    <div class="register-box-body text-center">        <p class="login-box-msg text-info"><?= Html::encode($this->title) ?></p>        <?php $form = ActiveForm::begin(['id' => 'request-password-reset-form']); ?>            <?= $form->field($model, 'email', $fieldOptions2)->textInput(['data-toggle' => 'tooltip', 'title' => $title, 'placeholder' => Yii::t('yii', 'Enter your email address')])->label(false); ?>        <?php ActiveForm::end(); ?>        <p><?=(Yii::$app->language == 'ru') ? 'или нажмите' : 'or click';?> <kbd>&crarr; enter</kbd></p>        <?=Html::a((Yii::$app->language == 'ru') ? 'Ура! я знаю пароль' : 'Sign In', '/login', ['class' => 'text-success']);?>        </div></div>